mapper.update:
UPDATE users
SET name         = #{name},
    email        = #{email},
    phone_number = #{phoneNumber},
    password     = #{password}
WHERE id = #{id};

mapper.delete:
DELETE
FROM users_addresses
WHERE user_id = #{userId}
  AND address_id = #{addressId}

mapper.select:
SELECT EXISTS(SELECT 1
              FROM users
              WHERE name = #{name}
                 OR email = #{email}
                 OR phone_number = #{phoneNumber});
